import react, { useState, useEffect } from 'react'
import Chart from 'react-apexcharts'

const proxy = "https://cors-anywhere.herokuapp.com/"
const newUrl = `${proxy}https://query1.finance.yahoo.com/v8/finance/chart/AAPL?symbol=AAPL&period1=1634841000&period2=1660501800&useYfid=true&interval=1d&includePrePost=true&events=div%7Csplit%7Cearn&lang=en-US&region=US&crumb=m%2FlQLK.F88U&corsDomain=finance.yahoo.com`

async function fetchData() {
  const response = await fetch(newUrl);
  return response.json();
}

const chart = {
  options: {
    chart: {
      type: 'candlestick',
      height: 350
    },
    title: {
      text: 'CandleStick Chart',
      align: 'left'
    },
    xaxis: {
      type: 'datetime'
    },
    yaxis: {
      tooltip: {
        enabled: true
      }
    }
  },
};

export default function Ticker() {
  const [price, setPrice] = useState(0)
  const [series, setSeries] = useState([{ 'data': [] }])

  useEffect(() => {
    let timeoutId;
    async function getLatestPrice() {
      try {
        const data = await fetchData();
        console.log(data);
        const stock = data.chart.result[0];
        setPrice(stock.meta.regularMarketPrice.toFixed(2));
        const quote = stock.indicators.quote[0];
        const prices = stock.timestamp.map((timestamp, index) => ({
          x: new Date(timestamp * 1000),
          y: [quote.open[index], quote.high[index], quote.low[index], quote.close[index]]
        }));
        setSeries([{
          data: prices,
        }]);
      } catch (error) {
        console.log(error);
      }
      // timeoutId = setTimeout(getLatestPrice, 5000 * 2);
    }

    getLatestPrice();

    return () => {
      clearTimeout(timeoutId);
    };
  }, []);
  return (
    <div>
      Ticker
      {price}
      <Chart options={chart.options} series={series} type="candlestick" width="100%" height={320} />
    </div>
  )
}
