import React, { useState, useEffect } from 'react';
import Ticker from './ticker';
import Stock from './stockInfo';
import Sentiment from './sentiment';

export default function Home() {
  const [stock, setStock] = useState('AAPL');
  const [action, setAction] = useState('');
  const [price, setPrice] = useState(0);
  const [prevPrice, setPrevPrice] = useState(0);
  const [timer, setTimer] = useState(60);

  function handleOptionsChange(event) {
    setStock(event.target.value);
  }

  function handleBuy() {
    setAction('BUY');
    setTimer(60);
    fetchPrice();
  }

  function handleSell() {
    setAction('SELL');
    setTimer(60);
    fetchPrice();
  }

  async function fetchPrice() {
    // ... existing code to fetch price

    // Start the timer countdown
    const countdown = setInterval(() => {
      setTimer(prevTimer => {
        if (prevTimer > 0) {
          return prevTimer - 1;
        } else {
          clearInterval(countdown);
          return prevTimer;
        }
      });
    }, 1000);
  }

  return (
    <div>
      <div className="ticker">
        <Ticker name={stock} />
      </div>
      <div>
        <select name="" id="" onChange={handleOptionsChange}>
          {/* ... options */}
        </select>
      </div>
      <div className="home-footer">
        <div className="stock-info-container">
          <Stock name={stock} />
        </div>

        <div className="buy-sell">
          <div className="buy">
            <button className="buybutt" onClick={handleBuy}>
              BUY
            </button>
          </div>

          <div className="sell">
            <button className="sellbutt" onClick={handleSell}>
              SELL
            </button>
          </div>
        </div>

        <div className="sentiment">
          <div className="stock-logo">logo</div>
          <div className="stock-sentiment">
            <Sentiment />
          </div>
        </div>
      </div>

    </div>
  );
}
