import React, { useState } from 'react';

const Carousel = () => {
  const video1 = 'https://www.youtube.com/embed/dQw4w9WgXcQ';
  const video2 = 'https://www.youtube.com/embed/some-video-url';
  const video3 = 'https://www.youtube.com/embed/another-video-url';

  const [activeSlide, setActiveSlide] = useState(0);

  const handleNextSlide = () => {
    setActiveSlide((prevSlide) => (prevSlide + 1) % 3);
  };

  const handlePrevSlide = () => {
    setActiveSlide((prevSlide) => (prevSlide - 1 + 3) % 3);
  };

  return (
    <div className="carousel-container">
      <div className="carousel-content">
        <div className={`carousel-slide ${activeSlide === 0 ? 'active' : ''}`}>
          <iframe src={video1} title="YouTube video 1" allowFullScreen></iframe>
        </div>
        <div className={`carousel-slide ${activeSlide === 1 ? 'active' : ''}`}>
          <iframe src={video2} title="YouTube video 2" allowFullScreen></iframe>
        </div>
        <div className={`carousel-slide ${activeSlide === 2 ? 'active' : ''}`}>
          <iframe src={video3} title="YouTube video 3" allowFullScreen></iframe>
        </div>
      </div>
      <button className="prev-button" onClick={handlePrevSlide}>{'<'}</button>
      <button className="next-button" onClick={handleNextSlide}>{'>'}</button>
    </div>
  );
};

export default Carousel;
