import React, { useState } from 'react';

const Carousel = () => {
  const videos = [
    'https://www.youtube.com/embed/dQw4w9WgXcQ',
    'https://www.youtube.com/embed/dQw4w9WgXcQ',
    'https://www.youtube.com/embed/another-video-url',
    // Add more video URLs here
  ];

  const [activeSlide, setActiveSlide] = useState(0);

  const handleNextSlide = () => {
    setActiveSlide((prevSlide) => (prevSlide + 1) % videos.length);
  };

  const handlePrevSlide = () => {
    setActiveSlide((prevSlide) => (prevSlide - 1 + videos.length) % videos.length);
  };

  return (
    <div className="carousel-container">
      <div className="carousel-content">
        {videos.map((video, index) => (
          <div
            key={index}
            className={`carousel-slide ${activeSlide === index ? 'active' : ''}`}
          >
            <iframe src={video} title="YouTube video player" allowFullScreen></iframe>
          </div>
        ))}
      </div>
      <button className="prev-button" onClick={handlePrevSlide}></button>
      <button className="next-button" onClick={handleNextSlide}></button>
    </div>
  );
};

export default Carousel;
