import React, { useState } from 'react';

const Carousel = () => {
  const [activeSlide, setActiveSlide] = useState(0);

  const handleNextSlide = () => {
    setActiveSlide((prevSlide) => (prevSlide + 1) % 2);
  };

  const handlePrevSlide = () => {
    setActiveSlide((prevSlide) => (prevSlide - 1 + 2) % 2);
  };

  return (
    <div className="carousel-container">
    <button className="carousel-button prev-button" onClick={handlePrevSlide}></button>
      <div className="carousel-content">
        {activeSlide === 0 && (
          <div className="carousel-slide">
            <iframe
              src="https://www.youtube.com/embed/dQw4w9WgXcQ"
              title="YouTube video player"
              allowFullScreen
            ></iframe>
          </div>
        )}
        {activeSlide === 1 && (
          <div className="carousel-slide">
            <iframe
              src="https://www.youtube.com/embed/dQw4w9WgXcQ"
              title="YouTube video player"
              allowFullScreen
            ></iframe>
          </div>
        )}
      </div>
      
      <button className="carousel-button next-button" onClick={handleNextSlide}></button>
    </div>
  );
};

export default Carousel;
