import React, { useState, useEffect } from 'react';
import Ticker from './ticker';
import Stock from './stockInfo';
import Sentiment from './sentiment';

export default function Home() {
  const [stock, setStock] = useState('AAPL');
  const [action, setAction]= useState("")
  const [price, setPrice] = useState(0);
  const [prevPrice, setPrevPrice] = useState(0);

  function handleOptionsChange(event) {
    setStock(event.target.value);
  }

  function handleBuy() {
    setAction("BUY")
    fetchPrice()
  }

  function handleSell() {
    setAction("SELL")
    fetchPrice()
  }

  async function fetchPrice() {
    const res = await fetch('http://localhost:8081');
    const Data = await res.json();

    const stock = Data
    console.log(stock)
    const currentPrice = stock.meta.regularMarketPrice.toFixed(2);
    setPrice(currentPrice);
    const currentTime = new Date();
    const hours = currentTime.getHours();
    const minutes = currentTime.getMinutes();
    const seconds = currentTime.getSeconds();

    console.log(`Current time is: ${hours}:${minutes}:${seconds}`); 
    if (prevPrice === 0) {
      setPrevPrice(currentPrice); 
      const currentTime = new Date();
      const hours = currentTime.getHours();
      const minutes = currentTime.getMinutes();
      const seconds = currentTime.getSeconds();

      console.log(`Current time is: ${hours}:${minutes}:${seconds}`);
    }
  }

  return (
    <div>
      <div className="ticker">
        <Ticker name={stock} />
      </div>
      <div>
        <select name="" id="" onChange={handleOptionsChange}>
          <option value="AAPL">AAPL</option>
          <option value="META">META</option>
          <option value="AMZN">AMZN</option>
          <option value="GOOGL">GOOGL</option>
          <option value="LICI.NS">LICI</option>
        </select>
      </div>
      <div className="home-footer">
        <div className="stock-info-container">
          <Stock name={stock} />
        </div>

        <div className="buy-sell">
          <div className="buy">
            <button className="buybutt" onClick={handleBuy}>
              BUY
            </button>
          </div>

          <div className="sell">
            <button className="sellbutt" onClick={handleSell}>
              SELL
            </button>
          </div>
        </div>

        <div className="sentiment">
          <div className="stock-logo">logo</div>
          <div className="stock-sentiment">
            <Sentiment />
          </div>
        </div>
      </div>
    </div>
  );
}
